"use strict";

function getModelParams(model) {
	var params = [];
	Object.keys(model.fields).forEach(function (field) {
		params.push({
			name: field,
			value: "$.params[\"" + model.name + "\"]." + field + "|0"
		});
	});
	return params;
}

exports = module.exports = {
	getModelParams: getModelParams
};