					var methodName = type !== 'server' ? (step.method ? '.' + step.method + '()' : '') : '';
					sectionTitle = '<span class="step-name">' + name + '</span> ' +
						'<span class="step-method">' + methodName + '</span> ' +
						'<span class="step-description">' + (step.description || '') + '</span>';
